import "./login.css"
import {Link} from "react-router-dom";
import { useContext, useRef } from "react";
import { Context } from "../../context/Context";
import axios from "axios";




export default function Login() { 
  const userRef = useRef();
  const passwordRef = useRef();
  const { dispatch, isFetching } = useContext(Context);

  
  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch()
  }
  return (
    <div className="login">
        <span className="loginTitle">Đăng nhập</span>
        <form className="loginForm" onSubmit={handleSubmit}>
            <label>Tài khoản</label>
            <input type="text" className="loginInput" placeholder="Nhập tài khoản của bạn..."
              ref={useRef}
            />
            <label>Mật Khẩu</label>
            <input type="password" className="loginInput" placeholder="Nhập mật khẩu của bạn..."
              ref={passwordRef} 
            />

            <button className="loginButton" typeel="submit" >Đăng nhập</button>
            
        </form>
        <button className="loginRegisterButton">
        <Link className="link" to ="/register">Đăng ký
        </Link>
        </button>
        
    </div>
  )
}
